import { Component, Input, OnInit } from '@angular/core';
import { EmailSettings } from 'src/models/emailSettings';
import { EmailSettingsService } from 'src/services/emailSettings.service';
import { FormBuilder, Validators } from '@angular/forms';

@Component({
  selector: 'app-inputs-block',
  templateUrl: './inputs-block.component.html',
  styleUrls: ['./inputs-block.component.scss']
})
export class InputsBlockComponent implements OnInit {
  emailSettings: EmailSettings = {
    intervalValue: 0, 
    startTime: 0,
    endTime:0,
    isScheduled: false,
    periodicity: '', 
    daysOfWeek: []
  };

  public emailSettingsForm = this.formBuilder.group({
    interval: this.emailSettings.intervalValue,
    startTime: this.emailSettings.startTime, 
    endTime: this.emailSettings.endTime,
    periodicity: this.emailSettings.periodicity
  })

 constructor(private emailSettingService: EmailSettingsService, private formBuilder: FormBuilder){}

  ngOnInit(): void {
   this.emailSettingService.getGeneralProperties().subscribe({
    next: (response: any) => {
      this.emailSettings = response,
      console.log(this.emailSettings)
    },
    error: error => console.log(error)
    });
 }

 onCheckboxDaysChange(additionalValue: number) {
  if (this.emailSettings.daysOfWeek.includes(additionalValue)) {
    const index = this.emailSettings.daysOfWeek.indexOf(additionalValue);
    if (index > -1) {
      this.emailSettings.daysOfWeek.splice(index, 1);
    }
  } else {
    this.emailSettings.daysOfWeek.push(additionalValue);
  }
  }

  onIsScheduledChange(){
    this.emailSettings.isScheduled = !this.emailSettings.isScheduled;
    console.log('Checkbox checked:', this.emailSettings.isScheduled);
  }

  onFormSubmit() {
    /*this.submitForm(formData)
      .subscribe(response => {
        console.log('POST request successful', response);
        // Handle the response as needed
      }, error => {
        console.error('Error sending POST request', error);
        // Handle the error
      });*/
  }








}
<h1>Schedule a New Job</h1>

<form>
    <div class="row">
            <div class="col-sm-6">
                <div class="form-group">
                    <label for="everyHourInput" class="form-label">Every:</label>
                    <input class="form-control" name="hourInput" formControlName="interval" type="text"/>
                </div>
                <div class="form-group">
                    <label for="hourFromInput" class="form-label">From Hour:</label>
                    <input class="form-control" name="fromHourInput" id="hourFromInput" formControlName="startTime" type="text" value="{{emailSettings.startTime}}"/>
                </div>

            </div>
            <div class="col-sm-6">
                <div class="form-group">
                    <label for="timeTypeInput">Time value:</label>
                        <select class="form-select" ngModel>
                            <option value="hours" [selected]="emailSettings.periodicity === 'hours'">Hours</option>
                            <option value="minutes" [selected]="emailSettings.periodicity === 'minutes'">Minutes</option>
                            <option value="seconds" [selected]="emailSettings.periodicity === 'seconds'">Seconds</option>
                        </select>
                </div>
                <div class="form-group">
                        <label for="hourToInput" class="form-label">To Hour:</label>
                        <input class="form-control" name="hourToInput" type="text" value="{{emailSettings.endTime}}"ngModel/>
                </div>
            </div>

    </div>

    <div class="row mt-3">
        <div class="col-md-3">
            <div class="form-check mb-3">
                <input class="form-check-input" type="checkbox" value="0" id="flexCheckDefault" #checkbox (change)="onCheckboxDaysChange(+checkbox.value)" [checked]="emailSettings.daysOfWeek.includes(0)">
                <label class="form-check-label fs-5" for="flexCheckDefault">
                Every Sunday
                </label>
            </div>
            <div class="form-check mb-3">
                <input class="form-check-input" type="checkbox" value="2" id="flexCheckDefault" #checkbox (change)="onCheckboxDaysChange(+checkbox.value)" [checked]="emailSettings.daysOfWeek.includes(2)">
                <label class="form-check-label fs-5" for="flexCheckDefault">
                Every Tuesday
                </label>
            </div>
            <div class="form-check mb-3">
                <input class="form-check-input" type="checkbox" value="4" id="flexCheckDefault" #checkbox (change)="onCheckboxDaysChange(+checkbox.value)" [checked]="emailSettings.daysOfWeek.includes(4)">
                <label class="form-check-label fs-5" for="flexCheckDefault">
                Every Thursday
                </label>
            </div>
            <div class="form-check mb-3">
                <input class="form-check-input" type="checkbox" value="6" id="flexCheckDefault" #checkbox (change)="onCheckboxDaysChange(+checkbox.value)" [checked]="emailSettings.daysOfWeek.includes(6)">
                <label class="form-check-label fs-5" for="flexCheckDefault">
                Every Saturday
                </label>
            </div>
        </div>
        <div class="col-md-3">
            <div class="form-check mb-3">
                <input class="form-check-input" type="checkbox" value="1" id="flexCheckDefault" #checkbox (change)="onCheckboxDaysChange(+checkbox.value)" [checked]="emailSettings.daysOfWeek.includes(1)">
                <label class="form-check-label fs-5" for="flexCheckDefault">
                Every Monday
                </label>
            </div>
            <div class="form-check mb-3">
                <input class="form-check-input" type="checkbox" value="3" id="flexCheckDefault" #checkbox (change)="onCheckboxDaysChange(+checkbox.value)" [checked]="emailSettings.daysOfWeek.includes(3)">
                <label class="form-check-label fs-5" for="flexCheckDefault">
                Every Wednesday
                </label>
            </div>
            <div class="form-check mb-3">
                <input class="form-check-input" type="checkbox" value="5" id="flexCheckDefault" #checkbox (change)="onCheckboxDaysChange(+checkbox.value)" [checked]="emailSettings.daysOfWeek.includes(5)">
                <label class="form-check-label fs-5" for="flexCheckDefault">
                Every Friday
                </label>
            </div>
            <div class="form-check mb-3">
                <input class="form-check-input" type="checkbox" value="" id="flexCheckDefault" (change)="onIsScheduledChange()" [checked]="emailSettings.isScheduled">
                <label class="form-check-label fs-5" for="flexCheckDefault">
                Is Scheduled 
                </label>
            </div>
        </div>
    </div>
    <div class="d-flex flex-row">
        <button type="button" class="btn btn-danger mr-3"><i class="fa-regular fa-xmark"></i>Cancel</button>
        <button type="submit" class="btn btn-success"><i class="fa-regular fa-floppy-disk"></i>Save</button>
    </div>

</form>
